mapper.update:
UPDATE board_${boardId}
SET
subject=#{subject},
contents=#{contents},
edit_date=SYSDATE
WHERE article_id=#{articleId}

UPDATE board_${boardId}
SET
view_count = view_count+1
WHERE article_id=#{articleId}


	SELECT view_count 
	FROM board_${boardId}
	WHERE article_id=#{articleId}

UPDATE board_${boardId}
SET
good_count = good_count+1
WHERE article_id=#{articleId}


	SELECT good_count 
	FROM board_${boardId}
	WHERE article_id=#{articleId}

UPDATE board_${boardId}
SET
bad_count = bad_count+1
WHERE article_id=#{articleId}


	SELECT bad_count 
	FROM board_${boardId}
	WHERE article_id=#{articleId}
